mapper.select:
select m.id, m.menu_name, m.path, m.component, m.icon, m.perms, m.type, m.require_auth, m.parent_id, m.enabled from sys_menu m inner join sys_role_menu rm on m.id = rm.menu_id inner join sys_role r on rm.role_id = r.id inner join sys_user_role ur on r.id = ur.role_id and ur.user_id = #{userId}
where ${searchKey} like concat('%',#{searchValue},'%')
order by m.create_time limit #{page}, #{size}

mapper.insert:
insert into sys_menu (
id, menu_name, path, component, icon, perms, type, require_auth,
parent_id, create_time, update_time
)
values (
#{id}, #{menuName}, #{path}, #{component} ,#{icon} , #{perms} ,#{type} , #{requireAuth} ,
#{parentId} , now(), now()
);
